import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
import DriverItem from "./DriverItem";
import PropTypes from "prop-types";
import Preloader from "../layout/Preloader";
import ButtonDriverProfile from "./ButtonDriverProfile";
// import logo from "../../img/Logo.png";
import M from "materialize-css/dist/js/materialize.min.js";

const Drivers = ({ getDrivers, driver: { drivers, loading } }) => {
  useEffect(() => {
    getDrivers();

    // eslint-disable-next-line
  }, []);

  if (loading || drivers === null) {
    return <Preloader />;
  }

  return (
    <div className="row" style={{ backgroundColor: "light-blue accent-1" }}>
      <div className="col s4">
        {/* Main left side */}
        <div className="row">
          <div className="col s10">
            <a href="#!" className="left">
              <img
                src="https://content-static.upwork.com/blog/uploads/sites/4/2016/11/01071306/Amy-Sept-profile.jpg"
                alt="profile_picture"
              />
            </a>
          </div>
          <div className="col s2">
            {/* <i class="fas fa-user-edit"></i> */}
            <i className="material-icons medium">edit</i>
          </div>
        </div>

        <div className="row">
          <span>
            <span className="grey-text">LICENSE NUMBER</span>
            <br />
            <span className="flow-text">RA2640AC</span>
          </span>
        </div>
        <br />
        <div className="row">
          <span>
            <span className="grey-text">EMAIL/USERNAME</span>
            <br />
            <span className="flow-text">sarawilson@gmail.com</span>
          </span>
        </div>
        <br />
        <div className="row">
          <span>
            <span className="grey-text">PHONE NUMBER</span>
            <br />
            <span className="flow-text">+1-555-424-3535</span>
          </span>
        </div>
      </div>

      <div className="col s8">
        {" "}
        {/* Main right side */}
        <ul className="right">
          <li>
            <img src="#!" alt="" />
          </li>
          <ButtonDriverProfile />
        </ul>
        <div className="col s12">
          <ul className="collection with-header">
            <li className="collection-header">
              <h4 className="center">Roy's Routes</h4>
            </li>

            {drivers.map(driver => (
              <DriverItem driver={driver} key={driver.id} />
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

Drivers.propTypes = {
  driver: PropTypes.object.isRequired,
  getDrivers: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  driver: state.driver
});

export default connect(mapStateToProps, { getDrivers })(Drivers);
