import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
import DriverItem from "./DriverItem";
import PropTypes from "prop-types";
import Preloader from "../layout/Preloader";
import logo from "../../img/Logo.png";

const Drivers = ({ getDrivers, driver: { drivers, loading } }) => {
  useEffect(() => {
    getDrivers();

    // eslint-disable-next-line
  }, []);

  if (loading || drivers === null) {
    return <Preloader />;
  }

  return (
    <div>
      <div className="row">
        <div className="col s6">
          <a href="#!" className="left">
            <img src="#!" alt="profile_picture" />
          </a>
        </div>
        <div className="col s6">
          <ul className="right">
            <li>
              <img src="#!" alt="" />
            </li>
            <div className="action-btn">
              <a className="btn-floating btn-large red">
                <i className="large material-icons">mode_edit</i>
              </a>
              <ul>
                <li>
                  <a className="btn-floating red">
                    <i className="material-icons">insert_chart</i>
                  </a>
                </li>
                <li>
                  <a className="btn-floating yellow darken-1">
                    <i className="material-icons">format_quote</i>
                  </a>
                </li>
                <li>
                  <a className="btn-floating green">
                    <i className="material-icons">publish</i>
                  </a>
                </li>
                <li>
                  <a className="btn-floating blue">
                    <i className="material-icons">attach_file</i>
                  </a>
                </li>
              </ul>
            </div>
          </ul>
        </div>
      </div>

      <div className="col s8">
        <ul className="collection with-header">
          <li className="collection-header">
            <h4 className="center">Roy's Routes</h4>
          </li>

          {drivers.map(driver => (
            <DriverItem driver={driver} key={driver.id} />
          ))}
        </ul>
      </div>
    </div>
  );
};

Drivers.propTypes = {
  driver: PropTypes.object.isRequired,
  getDrivers: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  driver: state.driver
});

export default connect(mapStateToProps, { getDrivers })(Drivers);
