import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
import { withRouter } from "react-router";
import DriverProfileList from "./DriverProfileList";
import Preloader from "../layout/Preloader";
import PropTypes from "prop-types";
// { driver: { drivers, loading }, getDrivers }
// const DriverProfile = props => {
const DriverProfile = props => {
  useEffect(() => {
    getDrivers();

    // eslint-disable-next-line
  }, []);

  if (props.loading || props.drivers === null) {
    return <Preloader />;
  }

  return (
    <div className="col s12">
      <ul className="with-header">
        <div>{props.driver.drivers.firstName}</div>
        {/* {props.driver.drivers &&
          props.driver.drivers.map(driver => {
            return <DriverProfileList driver={driver} key={driver.id} />;
          })} */}
        {props.driver &&
          props.driver.map(driver => {
            return <DriverProfileList driver={driver} key={driver.id} />;
          })}
        <div>drivers</div>
      </ul>
    </div>
  );
};

DriverProfile.propTypes = {
  driver: PropTypes.object.isRequired
};

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  let id = ownProps.match.params.driver_id;
  console.log(id);
  return {
    driver:
      state.driver.drivers &&
      state.driver.drivers.filter(driver => driver.id === id)
  };
};
//.find(driver => driver.id === id)

const mapDispatchToProps = dispatch => {
  return {
    getDrivers: () => dispatch(getDrivers(dispatch))
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(DriverProfile)
);
