import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
// import DriverProfileButton from "./DriverProfileButton";
import DriverProfileList from "./DriverProfileList";
import PropTypes from "prop-types";

// const DriverProfile = ({ driver: { drivers }, getDrivers }) => {
const DriverProfile = ({ driver, getDrivers }) => {
  const [routeId, setRouteId] = useState(driver.match.params.driver_id);
  useEffect(() => {
    getDrivers();

    // eslint-disable-next-line
  }, []);
  console.log();
  return (
    <div className="col s12">
      <ul className="with-header">
        {driver.drivers &&
          driver.drivers.map(driver => {
            return (
              <DriverProfileList driver={driver} key={driver.id} id={routeId} />
            );
          })}
      </ul>
    </div>
  );
};

DriverProfile.propTypes = {
  driver: PropTypes.object.isRequired
};

const mapStateToProps = (state, ownProps) => {
  let id = ownProps.match.params.driver_id;
  console.log(id);
  return {
    driver: state.driver,
    routeId: id
  };
};
// const mapStateToProps = state => {
//   return {
//     driver: state.driver
//   };
// };
//.find(driver => driver.id === id)

// const mapDispatchToProps = dispatch => {
//   return {
//     getDrivers: driver => dispatch(getDrivers(driver))
//   };
// };

export default connect(mapStateToProps, { getDrivers })(DriverProfile);
