import {
  SET_LOADING,
  DRIVERS_ERROR,
  GET_DRIVERS,
  ADD_DRIVER,
  DELETE_DRIVER
} from "./types";

export const getDrivers = () => async dispatch => {
  try {
    setLoading();

    const res = await fetch("/drivers");
    const data = await res.json();

    dispatch({
      type: GET_DRIVERS,
      payload: data
    });
  } catch (err) {
    dispatch({
      type: DRIVERS_ERROR,
      payload: err.response.statusText
    });
  }
};

export const addDriver = driver => async dispatch => {
  try {
    setLoading();

    const res = await fetch("/drivers", {
      method: "POST",
      body: JSON.stringify(driver),
      headers: {
        "Content-type": "application.json"
      }
    });
    const data = await res.json();

    dispatch({
      type: ADD_DRIVER,
      payload: data
    });
  } catch (err) {
    dispatch({
      type: DRIVERS_ERROR,
      payload: err.response.statusText
    });
  }
};

export const deleteDriver = id => async dispatch => {
  try {
    setLoading();

    await fetch(`/drivers/${id}`, {
      method: "DELETE"
    });

    dispatch({
      type: DELETE_DRIVER,
      payload: id
    });
  } catch (err) {
    console.log("err response", err);
    dispatch({
      type: DRIVERS_ERROR,
      payload: err.response.statusText
    });
  }
};

export const setLoading = () => {
  return {
    type: SET_LOADING
  };
};
