import React, { Component } from "react";
import { connect } from "react-redux";
import { storage } from "../../index";
import { addDriver } from "../../store/actions/driverActions";
import PropTypes from "prop-types";
import M from "materialize-css/dist/js/materialize.min.js";

export class ClassDriverModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: null,
      url: "",
      progress: 0,
      firstName: "",
      lastName: "",
      address: "",
      email: "",
      phoneNumber: "",
      birthYear: "",
      hourlyRate: "",
      license: "",
      available: true,
      completedRoutes: null,
      incompleteRoutes: null
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleUpload = this.handleUpload.bind(this);
    this.onSubmit = this.handleSubimt.bind(this);
  }

  handleChange = e => {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      this.setState(() => ({ image }));
    }
  };

  handleUpload = () => {
    // const { image } = this.state;
    const image = this.state.image;
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        // progress function
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress });
      },
      error => {
        console.log(error);
      },
      () => {
        // complete function
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            console.log(url);
            this.setState({ url });
          });
      }
    );
  };

  handleSubmit = e => {
    if (this.firstName === "" || this.lastName === "") {
      M.toast({ html: "Please enter the first and last name" });
    } else {
      this.props.addDriver(this.state);
      //   addDriver({
      //     firstName,
      //     lastName,
      //     address,
      //     email,
      //     phoneNumber,
      //     birthYear,
      //     hourlyRate,
      //     license,
      //     available,
      //     completedRoutes,
      //     incompleteRoutes,
      //     url
      //   });

      M.toast({
        html: `${this.firstName} ${this.lastName} was added as a driver`
      });
      // Clear Fields
      //   setFirstName("");
      //   setLastName("");
      //   setAddress("");
      //   setEmail("");
      //   setPhoneNumber("");
      //   setBirthYear("");
      //   setHourlyRate("");
      //   setLicense("");
      //   setAvailable(true);
      //   setCompletedRoutes(null);
      //   setIncompleteRoutes(null);
    }
  };

  render() {
    return (
      <div id="add-driver-modal" className="modal">
        <div className="modal-content">
          <div className="row">
            <div className="col s12">
              <h4>Add driver</h4>
              <br />

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="firstName"
                    value={this.firstName}
                    onChange={this.state.firstName}
                  />
                  <label htmlFor="firstName" className="active">
                    First Name
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="lastName"
                    value={this.lastName}
                    onChange={this.state.lastName}
                  />
                  <label htmlFor="lastName" className="active">
                    Last Name
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="email"
                    name="email"
                    value={this.email}
                    onChange={this.state.email}
                  />
                  <label htmlFor="email" className="active">
                    Email
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="address"
                    value={this.address}
                    onChange={this.state.address}
                  />
                  <label htmlFor="address" className="active">
                    Address
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="phoneNumber"
                    value={this.phoneNumber}
                    onChange={this.state.phoneNumber}
                  />
                  <label htmlFor="phoneNumber" className="active">
                    Phone Number
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="birthYear"
                    value={this.birthYear}
                    onChange={this.state.birthYear}
                  />
                  <label htmlFor="birthYear" className="active">
                    Birth Year
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="number"
                    name="hourlyRate"
                    value={this.hourlyRate}
                    onChange={this.state.hourlyRate}
                  />
                  <label htmlFor="hourlyRate" className="active">
                    Hourly Rate
                  </label>
                </div>
              </div>

              <div className="col s6">
                <div className="input-field">
                  <input
                    type="text"
                    name="license"
                    value={this.license}
                    onChange={this.state.license}
                  />
                  <label htmlFor="license" className="active">
                    License
                  </label>
                </div>
              </div>

              <div className="col s12">
                <div className="modal-footer">
                  <a href="#!">
                    <button
                      className="btn waves-effect blue waves-light"
                      type="submit"
                      name="action"
                      onClick={this.handleSubmit}
                    >
                      Submit
                      <i className="material-icons right">send</i>
                    </button>
                  </a>
                </div>
              </div>
              <div>
                <progress value={this.state.progress} max="100" />
                <br />
                <input type="file" onChange={this.handleChange} />
                <button onClick={this.handleUpload}>Upload</button>
                <br />
                <img src={this.state.url} alt="" height="300" width="400" />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ClassDriverModal.propTypes = {
  addDriver: PropTypes.func.isRequired
};

const mapDispatchToProps = dispatch => {
  return {
    addDriver: driver => dispatch(addDriver(driver))
  };
};

export default connect(null, mapDispatchToProps)(ClassDriverModal);
