import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
import { withRouter } from "react-router";
import { useSelector } from "react-redux";
import DriverProfileList from "./DriverProfileList";
import Preloader from "../layout/Preloader";
import PropTypes from "prop-types";
// { driver: { drivers, loading }, getDrivers }
// const DriverProfile = props => {
const DriverProfile = ownProps => {
  let id = ownProps.match.params.driver_id;
  const driver = useSelector(state => state.driver.drivers).filter(
    driver => driver.id === id
  );

  useEffect(() => {
    getDrivers();

    // eslint-disable-next-line
  }, []);

  if (driver.loading || driver.drivers === null) {
    return <Preloader />;
  }

  console.log(id);

  return (
    <div className="col s12">
      <ul className="with-header">
        <div>{driver.firstName}</div>
        {driver.filter(driver => driver.id === id).map(driver.firstName)}
        {/* {props.driver.drivers &&
          props.driver.drivers.map(driver => {
            return <DriverProfileList driver={driver} key={driver.id} />;
          })} */}
        {/* {driver &&
          driver.filter(driver => {
            return <DriverProfileList driver={driver} key={driver.id} />;
          })} */}
        <div>drivers</div>
      </ul>
    </div>
  );
};

DriverProfile.propTypes = {
  driver: PropTypes.object.isRequired
};

// const mapStateToProps = (state, ownProps) => {
//   console.log(state);
//   let id = ownProps.match.params.driver_id;
//   console.log(id);
//   return {
//     driver: state.driver.drivers
//   };
// };
//.find(driver => driver.id === id)

const mapDispatchToProps = dispatch => {
  return {
    getDrivers: () => dispatch(getDrivers(dispatch))
  };
};

export default withRouter(connect(null, mapDispatchToProps)(DriverProfile));
