import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDrivers } from "../../store/actions/driverActions";
import DriverProfileButton from "./DriverProfileButton";
import PropTypes from "prop-types";

const DriverProfile = ({ driver, getDrivers }) => {
  // const { driver } = driver;
  useEffect(() => {
    getDrivers();

    // eslint - disable - next - line;
  });

  return (
    <div className="row" style={{ backgroundColor: "light-blue accent-1" }}>
      <div className="col s12 z-depth-1">
        <span className="flow-text"> DRIVERS > 1234</span>
      </div>
      <br />
      <br />
      <br />
      {/* Main left side */}
      <div className="col s4">
        <div className="row">
          <div className="col s10 center">
            {/* <img
                style={{ marginLeft: "80px" }}
                className="responsive-img materialboxed circle"
                width="120px"
                src={driver.image}
                alt="profile_picture"
              /> */}
            <span className="flow-text center">{driver.firstName}12341234</span>
          </div>
          <div className="col s2">
            <span>
              <i className="fas fa-user-edit small"></i>
              <span style={{ fontWeight: "bold" }}>EDIT</span>
            </span>
          </div>
        </div>

        <div className="row">
          <span>
            <span className="grey-text">LICENSE NUMBER</span>
            <br />
            <span className="flow-text">1234124</span>
          </span>
        </div>
        <br />
        <div className="row">
          <span>
            <span className="grey-text">EMAIL/USERNAME</span>
            <br />
            <span className="flow-text">
              <a href="#!">sarawilson@gmail.com</a>
            </span>
          </span>
        </div>
        <br />
        <div className="row">
          <span>
            <span className="grey-text">PHONE NUMBER</span>
            <br />
            <span className="flow-text">
              <a href="#!">+1-555-424-3535</a>
            </span>
          </span>
        </div>
      </div>
      {/* Main right side */}
      <div className="col s8">
        <ul className="right">
          <li>
            <img src="#!" alt="" />
          </li>
          <DriverProfileButton />
        </ul>
        <div className="col s12">
          <ul className="with-header">
            <li className="collection-header">
              <h4 className="center">Sara's Logs</h4>
            </li>
            {/* {driver.drivers &&
                driver.drivers.map(driver => {
                  return <DriverProfileList driver={driver} key={driver.id} />;
                })} */}
          </ul>
        </div>
      </div>
    </div>
  );
};

DriverProfile.propTypes = {
  driver: PropTypes.object.isRequired
};

// const mapStateToProps = state => {
//   return {
//     driver: state.driver
//   };
// };

// const mapStateToProps = state => ({
//   driver: state.driver
// });

const mapStateToProps = (state, ownProps) => {
  let id = ownProps.match.params.driver_id;
  console.log(id);
  return {
    driver: state.driver
  };
};
//.find(driver => driver.id === id)
// const mapDispatchToProps = dispatch => {
//   return {
//     getDrivers: driver => dispatch(getDrivers(driver))
//   };
// };

// const mapStateToProps = (state, ownProps) => {
//   const id = ownProps.match.params.id;
//   const drivers = state.firestore.data.drivers;
//   const driver = drivers ? drivers[id] : null;
//   return {
//     driver: driver,
//     id: id,
//     auth: state.firebase.auth
//   };
// };

export default connect(mapStateToProps, { getDrivers })(DriverProfile);
