import {
  SET_LOADING,
  DRIVERS_ERROR,
  GET_DRIVERS,
  ADD_DRIVER
} from "../actions/types";

const initialState = {
  drivers: null,
  error: null,
  loading: false
};

export default (state = initialState, action) => {
  switch (action.type) {
    case GET_DRIVERS:
      return {
        ...state,
        loading: false
      };
    case ADD_DRIVER:
      return {
        ...state,
        drivers: [...state.drivers, action.payload],
        loading: false
      };
    case DRIVERS_ERROR:
      console.log(action.payload);
      return {
        ...state,
        error: action.payload
      };
    case SET_LOADING:
      return {
        ...state,
        loading: true
      };
    default:
      return state;
  }
};
